from pathlib import Path

#######################################################################################################################
# Variables - Do not edit this file!
#######################################################################################################################

# The sampling rate used by APA
apa_sampling_rate = 500
# Our used (desired) sampling rate. We use this sampling rate to resample the data while synchronizing 
sampling_rate = 250
# Amount of seconds per stimulus
window_secs = 10
# Number of different temperatures applied per subject during the study
num_temp = 5
# Number of repetitions applied per subject during the study. Trial= How often one temperature is applied per subject
num_repetitions = 8
# Number of stimuli applied per subject during the study
num_stim = 40
# Baseline temperature of the Medoc
baseline_temp= 32
# Number of sensors used
num_sensors= 9
# Name of the used sensors in PainMonit Heater - these are the headers in for the np files
sensor_names = ["Bvp", "Eda_E4", "Resp", "Eda_RB", "Ecg", "Emg"]
# Directory of the raw data
raw_dir = Path("dataset", "raw-data")
# Directory of the segmented data (np files)
np_dir = Path(raw_dir.parent, "np-dataset")